    digits = {"0":"๐ฌ","1":"๐ญ","2":"๐ฎ","3":"๐ฏ","4":"๐ฐ","5":"๐ฑ","6":"๐ฒ","7":"๐ณ","8":"๐ด","9":"๐ต"}
